import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import history from '../history';
import '../App.css';
import axios from 'axios';


function isNumeric(n) {
    return !isNaN(parseInt(n)) && isFinite(n);
}


export default class SignUp extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            mno: ''
        };
    }

    sendOtp = async () => {
        const e = 91 + this.state.mno;
        console.log(e);
        await axios.get("http://localhost:8000/verify/getcode", {
            params: {
                phonenumber: e,
                channel: 'sms'
            }
        })
            .then(data => history.push('/OtpPage', this.state))
            .catch(err => console.log(err));
    };

    render() {

        const useStyles = makeStyles((theme) => ({
            paper: {
                marginTop: theme.spacing(8),
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
            },
            avatar: {
                margin: theme.spacing(1),
                backgroundColor: theme.palette.primary.main,
            },
            form: {
                width: '100%', // Fix IE 11 issue.
                marginTop: theme.spacing(3),
            },

            submit: {
                margin: theme.spacing(3, 0, 2),
            },
        }));

        const classes = useStyles;

        return (
            <Container component="main" maxWidth="xs" >
                <CssBaseline />
                <div className={classes.paper}>
                    <Avatar className={classes.avatar}>

                    </Avatar>
                    <Typography component="h1" variant="h5">
                        Sign up
                    </Typography>
                    <div className={classes.form} noValidate>
                        <Grid container spacing={2}>
                            <Grid item xs={12}>
                                <TextField
                                    variant="outlined"
                                    required
                                    fullWidth
                                    name="mobile"
                                    label="Mobile No."
                                    type="number"
                                    id="mobile"
                                    value={this.state.mno}
                                    onChange={e => this.setState({mno: e.target.value})}
                                    maxLength="10"
                                    pattern="\d{10}"
                                />
                            </Grid>
                        </Grid>
                        <Button xs={12}
                            type="submit"
                            fullWidth
                            variant="contained"
                            disabled={(this.state.mno.length!==10) || !isNumeric(this.state.mno)} 
                            color="primary"
                            className={classes.submit}
                            onClick={this.sendOtp}>
                            Sign Up
</Button>
                        <Grid container justify="flex-end">
                            <Grid item xs={12}>
                                <Link href="./Login">
                                    Already have an account? Sign in
</Link>
                            </Grid>
                        </Grid>
                    </div>
                </div>
            </Container>
        );
    }
}
